function createInstance(n, fromModule) {
  //       [param*, result*]
  var type = [[], []];
  n.params.forEach(function (param) {
    type[0].push(param.valtype);
  });
  n.result.forEach(function (result) {
    type[1].push(result);
  });
  var code = n.body;
  return {
    type: type,
    code: code,
    module: fromModule,
    isExternal: false
  };
}

module.exports = {
  createInstance: createInstance
};